import "core-js/modules/es.array.push.js";
import axios from "axios";
import * as LANGUAGE from '../../store/language.json';
import SnackBar from "../Snackbar/SnackBar.vue";
// import UploadCustomerFiles from "@/assets/services/UploadCustomerFiles.vue";

export default {
  data() {
    return {
      menu_search_birthday: false,
      filters: [{
        text: this.translate('All'),
        value: 'all'
      }, {
        text: this.translate('Active'),
        value: 'Active'
      }, {
        text: this.translate('Disabled'),
        value: "Disabled"
      }],
      defaultFilter: 'All',
      isCreateMode: true,
      activePicker: null,
      date: null,
      search_birthday: null,
      menu: false,
      snackbar: {
        show: false,
        message: null,
        color: null
      },
      search_string: "",
      dialog: false,
      dialogDelete: false,
      headers: [
      // { text: "#", value: "id" },
      {
        text: "Date added",
        value: "created_at"
      }, {
        text: "First Name",
        value: "first_name"
      }, {
        text: "Last Name",
        value: "last_name"
      },
      // { text: "Email", value: "email" },
      {
        text: "Birthday",
        value: "birthday"
      }, {
        text: "Zip",
        value: "zip"
      }, {
        text: "Place",
        value: "place"
      }, {
        text: "Phone",
        value: "phone"
      }, {
        text: "Info",
        value: "info"
      }, {
        text: "Actions",
        value: "actions",
        align: "center"
      }],
      data: [],
      loading: true,
      options: {},
      totalCustomersTable: 0,
      create_customer_data: {
        salutation: "",
        first_name: "",
        last_name: "",
        email: "",
        street_no: "",
        birthday: "",
        zip: "",
        place: "",
        phone: "",
        fax: "",
        client_type_id: "1",
        user_id: "",
        canton_id: "",
        lang: "",
        source_id: ""
      },
      get_customer_data: [],
      create_costumer_document: {
        customer_id: "",
        category_id: "",
        document: ""
      },
      all_customer_documents: [],
      client_types: [],
      create_customer_note: {
        user_id: "",
        customer_id: "",
        note: ""
      },
      canton_select: [],
      lang_select: [],
      source_id: [],
      deleteCustomerId: 0,
      updateCustomerId: 0,
      defaultItem: {
        salutation: "",
        first_name: "",
        last_name: "",
        email: "",
        street_no: "",
        birthday: "",
        zip: "",
        place: "",
        phone: "",
        fax: "",
        client_type_id: "1",
        user_id: "",
        canton_id: "",
        lang: "",
        source_id: ""
      }
    };
  },
  computed: {
    formTitle() {
      return this.isCreateMode === true ? "New Customer" : "Edit Customer";
    },
    filtered_search_birthday() {
      if (!this.search_birthday) return;
      const [year, month, day] = this.search_birthday.split('-');
      return `${day}.${month}.${year}`;
    },
    filtered_date() {
      if (!this.create_customer_data.birthday) return;
      const [year, month, day] = this.create_customer_data.birthday.split('-');
      return `${day}.${month}.${year}`;
    }
  },
  watch: {
    defaultFilter(val) {
      this.getCustomersDatatable(val);
    },
    dialog(val) {
      val || this.close();
    },
    dialogDelete(val) {
      val || this.closeDelete();
    },
    options: {
      handler() {
        this.getCustomersDatatable();
      },
      deep: true
    },
    search_string: {
      handler() {
        this.getCustomersDatatable();
      },
      deep: true
    },
    menu(val) {
      val && setTimeout(() => this.activePicker = "YEAR");
    },
    search_birthday() {
      this.getCustomersDatatable();
    }
  },
  methods: {
    itemClicked(event) {
      this.$router.push('applications/' + event.id);
    },
    singleApplication(id) {
      this.$router.push('applications/' + id);
    },
    translate(word) {
      let wordIndex = null;
      LANGUAGE.forEach((lng, index) => {
        if (lng.english.toLowerCase() == word.toLowerCase()) {
          wordIndex = index;
        }
      });
      if (wordIndex === null) {
        return word;
      }
      if (this.$store.state.language == 'german') {
        return LANGUAGE[wordIndex].german;
      } else {
        return LANGUAGE[wordIndex].english;
      }
    },
    toggleCustomerStatus(id) {
      axios.post('/admin/toggle_customer', {
        customer_id: id
      }).then(response => {
        let message = "";
        if (response.data.message.toLowerCase() == "customer enabled") {
          message = "Enabled";
        } else {
          message = "Disabled";
        }
        this.snackbar = {
          message,
          color: "success",
          show: true,
          error: false
        };
        this.$refs.snackbar.toggleSnackbar(true);
      }).catch(err => console.log(err));
    },
    getCustomersDatatable(status_filter = 'All') {
      this.loading = true;
      const {
        sortBy,
        sortDesc,
        page,
        itemsPerPage
      } = this.options;
      let sortType;
      sortType = "asc";
      if (sortDesc[0]) {
        sortType = "desc";
      }
      axios.get("/customer/datatable?term=" + this.search_string, {
        params: {
          sort_by: sortBy[0],
          sort: sortType,
          page: page,
          per_page: itemsPerPage,
          status_filter,
          birthday: this.search_birthday
        }
      }).then(response => {
        console.log('rr', response.data);
        this.data = response.data.data;
        this.totalCustomersTable = response.data.total;
        this.loading = false;
        this.$nextTick(() => {
          const currentEl = this.$el.querySelector(".v-data-footer__pagination");
          const translated = currentEl.innerHTML.replace("of", this.translate("of"));
          currentEl.innerHTML = translated;
        });
      }).catch(error => {
        console.log(error);
      });
    },
    createCustomer() {
      axios.post("/customer", this.create_customer_data).then(response => {
        this.create_customer_data = {};
        this.snackbar = {
          message: response.data.message,
          color: "success",
          show: true
        };

        // axios.post("application/add", this.create_customer_data)
        //  .then(response => console.log(response.data))
        //  .catch(err => console.log(err));

        this.getCustomersDatatable();
      }).catch(error => {
        console.log(error);
      });
    },
    getCustomer(customer_id) {
      axios.get("/customer/" + customer_id).then(response => {
        this.create_customer_data = {};
        this.get_customer_data.push(response.data.data);
        this.create_customer_data.salutation = response.data.data.salutation;
        this.create_customer_data.first_name = response.data.data.first_name;
        this.create_customer_data.last_name = response.data.data.last_name;
        this.create_customer_data.email = response.data.data.email;
        this.create_customer_data.street_no = response.data.data.street_no;
        this.create_customer_data.birthday = response.data.data.birthday;
        this.create_customer_data.zip = response.data.data.zip;
        this.create_customer_data.place = response.data.data.place;
        this.create_customer_data.phone = response.data.data.phone;
        this.create_customer_data.fax = response.data.data.fax;
        this.create_customer_data.client_type_id = parseInt(response.data.data.client_type_id);
        this.create_customer_data.user_id = response.data.data.user_id;
        this.create_customer_data.canton_id = parseInt(response.data.data.canton_id);
        this.create_customer_data.lang = parseInt(response.data.data.lang);
        this.create_customer_data.source_id = parseInt(response.data.data.source_id);
      }).catch(error => {
        console.log(error);
      });
    },
    updateCustomer(customer_id) {
      axios.put("/customer/" + customer_id, this.create_customer_data).then(response => {
        this.snackbar = {
          message: response.data.message,
          color: "success",
          show: true
        };
      }).catch(error => {
        console.log(error);
      });
    },
    deleteCustomer(customer_id) {
      axios.delete("/customer/" + customer_id).then(response => {
        this.snackbar = {
          message: response.data.message,
          color: "success",
          show: true
        };
      }).catch(error => {
        console.log(error);
      });
    },
    getClientTypes() {
      axios.get("/client/types").then(response => {
        for (let $i = 0; response.data.data.length > 0; $i++) {
          this.client_types.push({
            text: response.data.data[$i].name,
            value: response.data.data[$i].id
          });
        }
      }).catch(error => {
        console.log(error);
      });
    },
    getCantonSelect() {
      axios.get("/canton").then(response => {
        for (let $i = 0; response.data.length > 0; $i++) {
          this.canton_select.push({
            text: response.data[$i].name,
            value: response.data[$i].id
          });
        }
      }).catch(error => {
        console.log(error);
      });
    },
    getLangSelect() {
      axios.get("/languages").then(response => {
        for (const [key, value] of Object.entries(response.data)) {
          this.lang_select.push({
            text: this.translate(value),
            value: key
          });
        }
      }).catch(error => {
        console.log(error);
      });
    },
    getSourceSelect() {
      axios.get("/sources").then(response => {
        for (let $i = 0; response.data.data.length > 0; $i++) {
          this.source_id.push({
            text: this.translate(response.data.data[$i].name),
            value: response.data.data[$i].id
          });
        }
      }).catch(error => {
        console.log(error);
      });
    },
    createCustomerDocument() {
      axios.post("/customer_document", this.create_costumer_document).then(() => {
        this.create_costumer_document = {};
        // alert(response.data.message);
      }).catch(error => {
        console.log(error);
      });
    },
    getCustomerDocument(document_id) {
      axios.get("/customer_document/" + document_id).then(response => {
        this.create_costumer_document = {};
        this.create_costumer_document.customer_id = response.data.data.customer_id;
        this.create_costumer_document.category_id = response.data.data.category_id;
        this.create_costumer_document.document = response.data.data.document;
      }).catch(error => {
        console.log(error);
      });
    },
    getAllCustomerDocuments() {
      axios.get("/customer_document").then(response => {
        this.all_customer_documents = [];
        this.all_customer_documents.push(response.data.data);
      }).catch(error => {
        console.log(error);
      });
    },
    deleteCustomerDocument(document_id) {
      axios.delete("/customer_document/" + document_id).then(response => {
        alert(response.data.message);
      }).catch(error => {
        console.log(error);
      });
    },
    createCustomerNote() {
      axios.post("/customernote", this.create_customer_note).then(response => {
        this.create_customer_note = {};
        alert(response.data.message);
      }).catch(error => {
        console.log(error);
      });
    },
    updateCustomerNote(note_id) {
      axios.put("/customernote/" + note_id, this.create_customer_note).then(response => {
        alert(response.data.message);
      }).catch(error => {
        console.log(error);
      });
    },
    deleteCustomerNote(note_id) {
      axios.delete("/customernote/" + note_id).then(response => {
        alert(response.data.message);
      }).catch(error => {
        console.log(error);
      });
    },
    saveDate(date) {
      this.$refs.menu.save(date);
    },
    openDialog() {
      this.create_customer_data = this.defaultItem;
      this.isCreateMode = true;
      this.dialog = true;
    },
    editCustomer(item) {
      this.isCreateMode = false;
      this.getCustomer(item.id);
      this.updateCustomerId = item.id;
      this.dialog = true;
    },
    deleteCustomerModal(item) {
      this.deleteCustomerId = item.id;
      this.dialogDelete = true;
    },
    deleteCustomerModalConfirm() {
      this.closeDelete();
      this.deleteCustomer(this.deleteCustomerId);
      this.getCustomersDatatable();
    },
    close() {
      this.dialog = false;
    },
    closeDelete() {
      this.dialogDelete = false;
    },
    save() {
      this.close();
      this.createCustomer();
      this.getCustomersDatatable();
    },
    update() {
      this.close();
      this.updateCustomer(this.updateCustomerId);
      this.getCustomersDatatable();
    }
  },
  mounted() {
    this.getCustomersDatatable();
    this.getClientTypes();
    this.getCantonSelect();
    this.getLangSelect();
    this.getSourceSelect();
    if (this.$router.currentRoute.name == 'home') {
      this.headers = [
      // { text: "#", value: "id" },
      {
        text: this.translate("Date added"),
        value: "created_at"
      }, {
        text: this.translate("First Name"),
        value: "first_name"
      }, {
        text: this.translate("Last Name"),
        value: "last_name"
      }, {
        text: this.translate("Actions"),
        value: "actions",
        align: 'center'
      }];
    } else {
      this.headers = [
      // { text: "#", value: "id" },
      {
        text: this.translate("Date added"),
        value: "created_at"
      }, {
        text: this.translate("First Name"),
        value: "first_name"
      }, {
        text: this.translate("Last Name"),
        value: "last_name"
      },
      // { text: "Email", value: "email" },
      {
        text: this.translate("Birthday"),
        value: "birthday"
      }, {
        text: this.translate("Zip"),
        value: "zip"
      }, {
        text: this.translate("Place"),
        value: "place"
      }, {
        text: this.translate("Phone"),
        value: "phone"
      }, {
        text: this.translate("Info"),
        value: "info"
      }, {
        text: this.translate('Status'),
        value: 'enabled_value'
      }, {
        text: this.translate("Actions"),
        value: "actions",
        align: "center"
      }];
    }
  },
  components: {
    SnackBar
  }
};
