import PeopleTab from "./PeopleTab.vue";
import NotesTab from "./NotesTab.vue";
import HistoryTab from "./HistoryTab.vue";
import ConsulationProtocolTab from "./ConsulationProtocolTab.vue";
import * as LANGUAGE from '../../../store/language.json';
export default {
  data() {
    return {
      tab: null,
      items: [{
        tab: "Family members",
        content: PeopleTab
      }, {
        tab: "Notes",
        content: NotesTab
      }, {
        tab: "History",
        content: HistoryTab
      }, {
        tab: "Consultation Protocol",
        content: ConsulationProtocolTab
      }]
    };
  },
  methods: {
    translate(word) {
      let wordIndex = null;
      LANGUAGE.forEach((lng, index) => {
        if (lng.english.toLowerCase() == word.toLowerCase()) {
          wordIndex = index;
        }
      });
      if (wordIndex === null) {
        return word;
      }
      if (this.$store.state.language == 'german') {
        return LANGUAGE[wordIndex].german;
      } else {
        return LANGUAGE[wordIndex].english;
      }
    }
  },
  components: {
    PeopleTab,
    NotesTab,
    HistoryTab,
    ConsulationProtocolTab
  }
};
